import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { deleteToDos, updateToDosStatus } from "../actions";

const Todo = ({ todo }) => {
  const dispatch = useDispatch();
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState("");
  const [showModal, setShowModal] = useState(false); // For modal visibility

  const confirmDelete = () => {
    dispatch(deleteToDos(todo.id))
      .then(() => {
        setMessage("Todo deleted successfully!");
        setMessageType("success");
        setTimeout(() => setMessage(""), 3000);
      })
      .catch((err) => {
        setMessage("Failed to delete todo.");
        setMessageType("error");
        console.error(err);
        setTimeout(() => setMessage(""), 3000);
      })
      .finally(() => setShowModal(false));
  };

  return (
    <>
      {message && (
        <div
          style={{
            color: messageType === "success" ? "green" : "red",
            marginBottom: "10px",
          }}
        >
          {message}
        </div>
      )}

      <tr>
        <td>{todo.id}</td>
        <td>{todo.title}</td>
        <td>{todo.description}</td>
        <td>
          <input
            type="checkbox"
            checked={todo.isCompleted}
            onChange={() =>
              dispatch(updateToDosStatus(todo.id, !todo.isCompleted))
            }
          />
        </td>
        <td>
          <button
            className="btn btn-primary btn-m"
            onClick={() =>
              dispatch(updateToDosStatus(todo.id, !todo.isCompleted))
            }
          >
            {todo.isCompleted ? "Undo" : "Complete"}
          </button>
        </td>
        <td>
          <button className="btn btn-danger" onClick={() => setShowModal(true)}>
            Delete
          </button>
        </td>
      </tr>

      {/* Modal */}
      {showModal && (
        <div className="modal fade show" style={{ display: "block" }}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Todo App asks!</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                Are you sure you want to delete this todo?
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={() => setShowModal(false)}>
                  Cancel
                </button>
                <button className="btn btn-danger" onClick={confirmDelete}>
                  Delete
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal backdrop */}
      {showModal && <div className="modal-backdrop fade show"></div>}
    </>
  );
};

export default Todo;
