import React,{ useState } from "react";
import { useDispatch } from "react-redux";
import { deleteToDos, updateToDosStatus } from "../actions";

const Todo = ({ todo }) => {


  const dispatch = useDispatch();

  const handleToggleStatus = () => {
    dispatch(updateToDosStatus(todo.id, !todo.isCompleted));
  };

  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete this todo?")) {
      dispatch(deleteToDos(todo.id))
        .then(() => {
          setMessage("Todo deleted successfully!");
          setMessageType("success");
          setTimeout(() => setMessage(""), 3000);
        })
        .catch((err) => {
          setMessage("Failed to delete todo.");
          setMessageType("error");
          console.error(err);
          setTimeout(() => setMessage(""), 3000);
        });
    }
  };

  const handleUpdate = () => {
    dispatch(updateToDosStatus(todo.id, !todo.isCompleted))
      .then((response) => {
        console.log("Toggled completion status", response);
      })
      .catch((err) => {
        console.error("Failed to toggle status", err);
      });
  };

  return (

      <tr>
        <td>{todo.id}</td>
        <td>{todo.title}</td>
        <td>{todo.description}</td>
        <td>
          <input
            type="checkbox"
            checked={todo.isCompleted}
            onChange={handleToggleStatus}
          />
        </td>
        <td>
          <button className="btn btn-primary btn-m" onClick={handleUpdate}>
            {todo.isCompleted ? "Undo" : "Complete"}
          </button>
        </td>
        <td>
          <button className="btn btn-danger btn-sm" onClick={handleDelete}>
            Delete
          </button>
        </td>
      </tr>

  );
};

export default Todo;
